from fastapi import FastAPI # 导入FastAPIimport uvicornapp=FastAPI()# 添加首页# 网页根目录，根据url得到响应信息@app.get("/")# 响应信息def index():    """这是首页"""    return "This is Home Page457"# 添加users页@app.get("/users")def users():    """获取所有的用户"""    return {"msg": "Get all users","code": 278}# 添加projects页@app.get("/projects")def projects():    return ["项目1","项目2"]if __name__ =='__main__':    uvicorn.run(app)